package cn.com.clearlight.io.transstream.demo;import java.io.IOException;import java.io.InputStream;public class IOrnTest {    public static void main(String[] args) throws IOException {        InputStream in = System.in;        StringBuilder sb = new StringBuilder();        int ch = 0;        while ((ch = in.read()) != -1) {            if (ch == '\r') {                System.out.println("\\r");            }            if (ch == '\n') {                System.out.println("\\n");            }            sb.append((char) ch);            System.out.println("数据有:" + sb.toString());            System.out.println("长度为:" + sb.length());        }    }}